package com.ly666.project.util;import org.springframework.stereotype.Service;import org.springframework.util.StringUtils;import java.util.HashMap;import java.util.Map;/** * @author ：luoyi * @date ：Created in 2019/5/11 10:16 * @desc : {自定义缓存服务} */@Servicepublic final class MyCache<T> {	private Map<String,T> map = new HashMap<>();	public boolean add(String key,T value){		if (map.containsKey(key)){			return false;		}else {			map.put(key,value);			return true;		}	}	public boolean remove(String key){		if (StringUtils.isEmpty(key)){			return false;		}		if (map.containsKey(key)){			map.remove(key);			return true;		}else{			return false;		}	}	public T get(String key){		if (StringUtils.isEmpty(key)){			return null;		}		return map.get(key);	}	public boolean update(String key,T value){		if (StringUtils.isEmpty(key)){			return false;		}		if (map.containsKey(key)){			map.put(key,value);			return true;		}else{			return false;		}	}	public boolean isExist(String key){		if (StringUtils.isEmpty(key)){			return false;		}		return map.containsKey(key);	}}