package com.ly666.project.util;import com.ly666.project.global.defaultValue.GlobalProperties;import org.apache.shiro.crypto.hash.Md5Hash;/** * @author ：luoyi * @date ：Created in 2019/5/13 20:24 * @desc : {签名/加密/摘要服务工具类} */public class ShiroUtil {	/**	 * shiro密码加密工具类	 *	 * @param credentials 密码	 * @return	 */	public static String generaPassword(String credentials) {		return new Md5Hash(credentials).toString();	}	/**	 * shiro密码加密工具类	 *	 * @param credentials 密码	 * @param saltSource  密码盐	 * @return	 */	public static String generaPassword(String credentials, String saltSource) {		return new Md5Hash(credentials, saltSource + GlobalProperties.HASH_SALT, GlobalProperties.HASH_ITERATIONS).toString();	}}